    private byte[] convertBase64toByte(String base64String){
        byte[] valueDecoded = new byte[0];
        try {
            valueDecoded = java.util.Base64.getDecoder().decode(base64String.getBytes(StandardCharsets.UTF_8));
            //valueDecoded = Base64.getDecoder().decode(base64String.getBytes(StandardCharsets.UTF_8));

        } catch (Exception ex) {
        }
        return valueDecoded;
    }

    private String convertBytetoBase64(byte[] byteCode){
        byte[] valueDecoded = new byte[0];
        try {
            valueDecoded = java.util.Base64.getEncoder().encode(byteCode);

        } catch (Exception ex) {
        }
        return new String(valueDecoded);
    }
